#include <stdio.h>

void swap(int *a, int *b)
{
    int tmp = *a;
    *a = *b;
    *b = tmp;
}

/* 选择排序 并返回交换次数 */
int selectionSort(int A[], int N)
{
    int count = 0, pivot;
    for (int i = 0; i < N - 1; i++) {
        pivot = i;
        for (int j = i + 1; j < N; j++) {
            if (A[j] < A[pivot])
                pivot = j;
        }
        // 如果本次循环 pivot刚好是最小值 则pivot不会改变，也就不用交换
        if (pivot != i) {
            swap(&A[i], &A[pivot]);
            count++;
        }
    }

    return count;
}

int main()
{
    int A[100], N, count;
    scanf('%d', &N);
    for (int i = 0; i < N; i++)
        scanf('%d', &A[i]);

    count = selectionSort(A, N);

    for (int i = 0; i < N; i++) {
        if (i > 0)
            printf(' ');
        printf('%d', A[i]);
    }
    printf('\n%d\n', count);

    return 0;
}
```。

