#include <stdio.h>

/* 按照顺序输出数组元素 */
void trace(int A[], int N)
{
    for (int i = 0; i < N; i++) {
        if (i > 0)
            printf(' '); // 相邻元素之间留空格
        printf('%d', A[i]);
    }
    printf('\n');
}

/* 插入排序 */
void insertionSort(int A[], int N)
{
    int j, v;
    for (int i = 1; i < N; i++) {
        v = A[i];
        j = i - 1;
        while (j >= 0 && A[j] > v) {
            A[j + 1] = A[j];
            j--;
        }
        A[j + 1] = v;
        trace(A, N);
    }
}

int main()
{
    int N;
    int A[100];
    scanf('%d', &N);
    for (int i = 0; i < N; i++)
        scanf('%d', &A[i]);
    trace(A, N);

    insertionSort(A, N);

    return 0;
}
```。

