#include <stdio.h>

void swap(int *a, int *b)
{
    int tmp = *a;
    *a = *b;
    *b = tmp;
}

/* 冒泡排序 并返回交换次数 */
int bubbleSort(int A[], int N)
{
    int count = 0;
    for (int i = N - 1; i > 0; i--) {
        for (int j = 0; j < i; j++) {
            if (A[j] > A[j + 1]) {
                swap(&A[j], &A[j + 1]);
                count++;
            }
        }
    }
    return count;
}

int main()
{
    int A[100], N, count;
    scanf('%d', &N);
    for (int i = 0; i < N; i++)
        scanf('%d', &A[i]);

    count = bubbleSort(A, N);

    for (int i = 0; i < N; i++) {
        if (i > 0)
            printf(' ');
        printf('%d', A[i]);
    }
    printf('\n%d\n', count);

    return 0;
}
```。

