#include <stdio.h>
#include <stdlib.h>

/* 用数组实现栈 */
int top, A[100];

void push(int x)
{
    /* 将元素插入top当前位置 top指向下一个位置 */
    A[top++] = x;
}

int pop()
{
    /* top当前值是未初始化的 返回top前面位置的值*/
    return A[--top];
}

int main()
{
    int a, b;
    top = 0;
    char s[100];

    while (scanf('%s', s) != EOF) {
        if (s[0] == '+') {
            a = pop();
            b = pop();
            push(a + b);
        } else if (s[0] == '-') {
            b = pop();
            a = pop();
            push(a - b);
        } else if (s[0] == '*') {
            a = pop();
            b = pop();
            push(a * b);
        } else {
            push(atoi(s));
        }
    }

    printf('%d\n', pop());

    return 0;
}
```。

