#include <stdio.h>

#define N 200000

int main()
{
    int a[N], num;
    scanf('%d', &num);
    for (int i = 0; i < num; i++) {
        scanf('%d', &a[i]);
    }

    int imin = a[0];
    int imax = a[1];
    int ans = a[1] - a[0];
    for (int i = 1; i < num; i++) {
        // 每次到下标i 更新最大值 以及最大利润
        if (a[i] > imax) {
            imax = a[i];
            ans = a[i] - imin;
        }
        // 看看最小值是不是要更新
        if (a[i] < imin) {
            imin = a[i];
        }
    }
    printf('%d\n', ans);

    return 0;
}
```。
